INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('In Search of Lost Time', 'Marcel Proust', 'Grasset', '1913-09-16', 'Modernist', 'In Search of Lost Time is a fictional autobiography by a man whose life almost mirrors that of Marcel Proust. The first forty pages of the novel describe the narrator as a young boy in bed awaiting, and as a middle-aged man remembering, his mothers good-night kiss.', 4215, 'In_Search_Of_Lost_Time.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('Ulysses', 'James Joyce', 'Sylvia Beach', '1922-02-02', 'Modernist', 'Stylistically dense and exhilarating, it is generally regarded as a masterpiece and has been the subject of numerous volumes of commentary and analysis. The novel is constructed as a modern parallel to Homers Odyssey.', 730, 'ulysses.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('Don Quixote', 'Miguel de Cervantes', 'Francisco de Robles', '1605-05-18', 'Novel', 'Honest, dignified, proud, and idealistic, he wants to save the world. As intelligent as he is mad, Don Quixote starts out as an absurd and isolated figure and ends up as a pitiable and lovable old man whose strength and wisdom have failed him.', 863, 'Don_Quixote.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('The Great Gatsby', 'F. Scott Fitzgerald', 'Charles Scribners Sons', '1925-04-10', 'Tragedy', 'Jay Gatsby - The title character and protagonist of the novel, Gatsby is a fabulously wealthy young man living in a Gothic mansion in West Egg. He is famous for the lavish parties he throws every Saturday night, but no one knows where he comes from, what he does, or how he made his fortune.', 218, 'The_Great_Gatsby.jpeg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('One Hundred Years of Solitude', 'Gabriel Garcia Marquez', 'Editorial Sudamericana', '1967-10-12', 'Magic Realism', 'One Hundred Years of Solitude is the history of the isolated town of Macondo and of the family who founds it, the Buendías. For years, the town has no contact with the outside world, except for gypsies who occasionally visit, peddling technologies like ice and telescopes.', 448, 'One_Hundred_Years_of_Solitude.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('Moby Dick', 'Herman Melville', 'Harper & Brothers', '1851-10-18', 'Adventure Fiction', 'The book is sailor Ishmaels narrative of the obsessive quest of Ahab, captain of the whaling ship Pequod, for revenge on Moby Dick, the giant white sperm whale that on the ship''s previous voyage bit off Ahabs leg at the knee.', 585, 'Moby_Dick.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('War and Peace', 'Leo Tolstoy', 'The Russian Messenger', '1865-01-11', 'Novel', 'War and Peace broadly focuses on Napoleon’s invasion of Russia in 1812 and follows three of the most well-known characters in literature: Pierre Bezukhov, the illegitimate son of a count who is fighting for his inheritance and yearning for spiritual fulfillment; Prince Andrei Bolkonsky, who leaves his family behind to fight in the war against Napoleon; and Natasha Rostov, the beautiful young daughter of a nobleman who intrigues both men.', 1225, 'War_and_Peace.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('Lolita', 'Vladimir Nabokov', 'Olympia Press', '1955-12-04', 'Novel', 'Lolita, of the Confession of a White Widowed Male by Vladimir Nabokov is a story about Humbert Humbert, a literature professor in his late thirties, obsessed with a twelve-year-old Dolores Haze.', 336, 'Lolita.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('Hamlet', 'William Shakespeare', 'Simon & Schuster', '1599-10-17', 'Tragedy', 'Hamlet is a story of how the ghost of a murdered king comes to haunt the living with tragic consequences. A vengeful ghost and a brothers murder, dominate the gloomy landscape of Hamlets Denmark.', 500, 'Hamlet.jpg');
INSERT INTO books (title, author, publisher, local_date, genre, description, no_of_pages, picture) VALUES ('The Catcher in the Rye', 'J. D. Salinger', 'Little, Brown and Company', '1951-07-16', 'Realistic Fiction', 'The novel details two days in the life of 16-year-old Holden Caulfield after he has been expelled from prep school. Confused and disillusioned, Holden searches for truth and rails against the “phoniness” of the adult world.', 234, 'The_Catcher_in_the_Rye.jpg');
